import React, { useState, useEffect } from 'react';
import { useParams, Link, useNavigate } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';
import blogService from '../services/blogService';
import './BlogPost.css';

const BlogPost = () => {
    const { slug } = useParams();
    const { user, isAuthenticated } = useAuth();
    const navigate = useNavigate();
    const [post, setPost] = useState(null);
    const [comments, setComments] = useState([]);
    const [newComment, setNewComment] = useState('');
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');
    const [commentLoading, setCommentLoading] = useState(false);

    useEffect(() => {
        const loadPost = async () => {
            try {
                setLoading(true);
                const response = await blogService.getPostBySlug(slug);
                setPost(response.post);
                setComments(response.post.comments || []);
            } catch (err) {
                setError('Failed to load blog post');
                console.error('Error loading post:', err);
            } finally {
                setLoading(false);
            }
        };

        loadPost();
    }, [slug]);



    const handleCommentSubmit = async (e) => {
        e.preventDefault();
        if (!newComment.trim()) return;

        setCommentLoading(true);
        try {
            const response = await blogService.createComment(post.id, newComment);
            setComments([response.comment, ...comments]);
            setNewComment('');
        } catch (err) {
            alert('Failed to add comment');
        } finally {
            setCommentLoading(false);
        }
    };

    const handleDeleteComment = async (commentId) => {
        if (!window.confirm('Are you sure you want to delete this comment?')) return;

        try {
            await blogService.deleteComment(commentId);
            setComments(comments.filter(c => c.id !== commentId));
        } catch (err) {
            alert('Failed to delete comment');
        }
    };

    const formatDate = (dateString) => {
        return new Date(dateString).toLocaleDateString('en-US', {
            year: 'numeric',
            month: 'long',
            day: 'numeric',
            hour: '2-digit',
            minute: '2-digit'
        });
    };

    if (loading) {
        return <div className="loading">Loading...</div>;
    }

    if (error || !post) {
        return (
            <div className="error-container">
                <h2>Post not found</h2>
                <p>{error}</p>
                <Link to="/blog">Back to Blog</Link>
            </div>
        );
    }

    const canEdit = user && (user.id === post.author_id || user.role === 'admin');

    return (
        <div className="blog-post-container">
            <article className="blog-post">
                <div className="post-header">
                    <Link to="/blog" className="back-link">← Back to Blog</Link>
                    {canEdit && (
                        <div className="post-actions">
                            <button 
                                onClick={() => navigate(`/blog/edit/${post.id}`)}
                                className="edit-btn"
                            >
                                Edit Post
                            </button>
                        </div>
                    )}
                </div>

                {post.featured_image && (
                    <div className="post-image">
                        <img src={post.featured_image} alt={post.title} />
                    </div>
                )}

                <h1 className="post-title">{post.title}</h1>

                <div className="post-meta">
                    <span className="author">
                        By {post.author_first_name} {post.author_last_name}
                    </span>
                    <span className="date">
                        {formatDate(post.published_date || post.created_date)}
                    </span>
                    <span className="views">
                        👁 {post.view_count} views
                    </span>
                </div>

                {post.categories && (
                    <div className="post-categories">
                        {post.categories.split(', ').map((cat, index) => (
                            <span key={index} className="category-tag">{cat}</span>
                        ))}
                    </div>
                )}

                <div 
                    className="post-content"
                    dangerouslySetInnerHTML={{ __html: post.content }}
                />

                <div className="comments-section">
                    <h3>Comments ({comments.length})</h3>

                    {isAuthenticated ? (
                        <form onSubmit={handleCommentSubmit} className="comment-form">
                            <textarea
                                value={newComment}
                                onChange={(e) => setNewComment(e.target.value)}
                                placeholder="Write a comment..."
                                rows="4"
                                required
                            />
                            <button type="submit" disabled={commentLoading}>
                                {commentLoading ? 'Posting...' : 'Post Comment'}
                            </button>
                        </form>
                    ) : (
                        <div className="login-prompt">
                            <p>Please <Link to="/login">login</Link> to comment</p>
                        </div>
                    )}

                    <div className="comments-list">
                        {comments.length === 0 ? (
                            <p className="no-comments">No comments yet. Be the first to comment!</p>
                        ) : (
                            comments.map(comment => (
                                <div key={comment.id} className="comment">
                                    <div className="comment-header">
                                        <span className="comment-author">
                                            {comment.first_name} {comment.last_name}
                                        </span>
                                        <span className="comment-date">
                                            {formatDate(comment.created_date)}
                                        </span>
                                    </div>
                                    <p className="comment-content">{comment.content}</p>
                                    {user && (user.id === comment.user_id || user.role === 'admin') && (
                                        <button
                                            onClick={() => handleDeleteComment(comment.id)}
                                            className="delete-comment"
                                        >
                                            Delete
                                        </button>
                                    )}
                                </div>
                            ))
                        )}
                    </div>
                </div>
            </article>
        </div>
    );
};

export default BlogPost;
