import React from 'react';
import { Link, useNavigate, useLocation } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';
import './Header.css';

const Header = () => {
    const { user, logout, isAuthenticated } = useAuth();
    const navigate = useNavigate();
    const location = useLocation();

    const handleLogout = () => {
        logout();
        navigate('/login');
    };

    const isActive = (path) => {
        return location.pathname === path ? 'active' : '';
    };

    return (
        <header className="app-header">
            <div className="header-container">
                <div className="header-left">
                    <Link to="/" className="brand">
                        <span className="brand-icon">üè¢</span>
                        <span className="brand-text">Company Portal</span>
                    </Link>
                </div>

                <nav className="header-nav">
                    <Link to="/" className={`nav-link ${isActive('/')}`}>
                        Home
                    </Link>
                    <Link to="/blog" className={`nav-link ${isActive('/blog')}`}>
                        Blog
                    </Link>
                    {isAuthenticated && (
                        <>
                            <Link to="/dashboard" className={`nav-link ${isActive('/dashboard')}`}>
                                My Dashboard
                            </Link>
                            <Link to="/blog/new" className={`nav-link btn-create-post`}>
                                <span>‚úçÔ∏è</span> Create Post
                            </Link>
                        </>
                    )}
                </nav>

                <div className="header-right">
                    {isAuthenticated ? (
                        <div className="user-section">
                            <div className="user-info">
                                <span className="user-avatar">
                                    {user?.first_name?.[0]}{user?.last_name?.[0]}
                                </span>
                                <div className="user-details">
                                    <span className="user-name">
                                        {user?.first_name} {user?.last_name}
                                    </span>
                                    <span className="user-role">{user?.role}</span>
                                </div>
                            </div>
                            <button onClick={handleLogout} className="btn-logout">
                                <span>üö™</span> Logout
                            </button>
                        </div>
                    ) : (
                        <Link to="/login" className="btn-header-login">
                            <span>üîê</span> Employee Login
                        </Link>
                    )}
                </div>
            </div>
        </header>
    );
};

export default Header;
