import React, { useState, useEffect } from 'react';
import eventService from '../services/eventService';
import './EventsSection.css';

const EventsSection = () => {
  const [currentDate, setCurrentDate] = useState(new Date());
  const [events, setEvents] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    fetchUpcomingEvents();
  }, []);

  const fetchUpcomingEvents = async () => {
    try {
      setLoading(true);
      const response = await eventService.getUpcomingEvents(10);
      if (response.success) {
        setEvents(response.data);
      } else {
        setError('Failed to fetch events');
      }
    } catch (error) {
      console.error('Error fetching events:', error);
      setError('Error loading events');
    } finally {
      setLoading(false);
    }
  };

  const getDaysInMonth = (date) => {
    return new Date(date.getFullYear(), date.getMonth() + 1, 0).getDate();
  };

  const getFirstDayOfMonth = (date) => {
    return new Date(date.getFullYear(), date.getMonth(), 1).getDay();
  };

  const renderCalendar = () => {
    const daysInMonth = getDaysInMonth(currentDate);
    const firstDay = getFirstDayOfMonth(currentDate);
    const days = [];

    // Add empty cells for days before month starts
    for (let i = 0; i < firstDay; i++) {
      days.push(<div key={`empty-${i}`} className="calendar-day empty"></div>);
    }

    // Add days of the month
    for (let day = 1; day <= daysInMonth; day++) {
      const hasEvent = events.some(e => e.date === day);
      days.push(
        <div key={day} className={`calendar-day ${hasEvent ? 'has-event' : ''} ${day === 30 ? 'selected' : ''}`}>
          {day}
        </div>
      );
    }

    return days;
  };

  const monthYear = currentDate.toLocaleDateString('en-US', { month: 'long', year: 'numeric' });

  return (
    <section className="events-section">
      <h2 className="section-title text-orange">Events</h2>

      <div className="calendar-container">
        <div className="calendar-header">
          <button className="nav-btn">‹</button>
          <span className="month-year">{monthYear}</span>
          <button className="nav-btn">›</button>
        </div>

        <div className="calendar-weekdays">
          <div>S</div>
          <div>M</div>
          <div>T</div>
          <div>W</div>
          <div>T</div>
          <div>F</div>
          <div>S</div>
        </div>

        <div className="calendar-grid">
          {renderCalendar()}
        </div>
      </div>

      <div className="upcoming-events">
        {events.map((event, index) => (
          <div key={index} className="event-item">
            <div className="event-date">{event.date}</div>
            <div className="event-details">
              <h4>{event.title}</h4>
              <p>{event.time}</p>
            </div>
            <button className="reminder-btn">Reminder</button>
          </div>
        ))}
      </div>
    </section>
  );
};

export default EventsSection;
